import Livro from "../modelo";

const livros: Array<Livro> = [
  new Livro(1, 1, "Livro 1", "Resumo do Livro 1", ["Autor 1"]),
  new Livro(2, 2, "Livro 2", "Resumo do Livro 2", ["Autor 2"]),
  new Livro(3, 1, "Livro 3", "Resumo do Livro 3", ["Autor 3"]),
];

class ControleLivro {
  obterLivros(): Array<Livro> {
    return livros;
  }

  incluir(novoLivro: Livro): void {
    const maiorCodigo = Math.max(...livros.map((livro) => livro.codigo));

    novoLivro.codigo = maiorCodigo + 1;

    livros.push(novoLivro);
  }

  excluir(codigo: number): void {
    const index = livros.findIndex((livro) => livro.codigo === codigo);
    if (index !== -1) {
      livros.splice(index, 1);
    }
  }
}

export default ControleLivro;
